import useInput from '../hooks/use-input'

const BasicForm = (props) => {
  const {
    value: enteredName,
    hasError: nameInputHasError,
    valueChangeHandler: nameInputChangeHandler,
    inputBlurHandler: nameInputBlurHandler,
    isValid: nameInputIsValid,
    reset: nameValueReset
  } = useInput(value => value.trim() !== '');


  const {
    value: enteredLastName,
    hasError: lastNameInputHasError,
    valueChangeHandler: lastNameInputChangeHandler,
    inputBlurHandler: lastNameInputBlurHandler,
    isValid: lastNameInputIsValid,
    reset: lastNameValueReset
  } = useInput(value => value.trim() !== '');

  const {
    value: enteredEmail,
    hasError: emailnputHasError,
    valueChangeHandler: emailInputChangeHandler,
    inputBlurHandler: emailInputBlurHandler,
    isValid: emailInputIsValid,
    reset: emailValueReset
  } = useInput(value => value.includes('@'));


  let formIsValid = false;
  if (nameInputIsValid && lastNameInputIsValid && emailInputIsValid) {
    formIsValid = true;
  }

  const formSubmitHandler = (event) => {
    event.preventDefault();
    nameValueReset();
    lastNameValueReset()
    emailValueReset()

    if (!nameInputIsValid && !lastNameInputIsValid && !emailInputIsValid) {
      return
    }
    console.log(enteredName);
    console.log(enteredLastName);
    console.log(enteredEmail);
  }

  const formInputClasses = nameInputHasError && lastNameInputHasError && emailInputHasError ? 'form-control invalid' : 'form-control'

  return (
    <form onSubmit={formSubmitHandler}>
      <div className='control-group'>
        <div className={formInputClasses}>
          <label htmlFor='name' >First Name</label>
          <input type='text' id='name' value={enteredName} onChange={nameInputChangeHandler} onBlur={nameInputBlurHandler} />
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' value={enteredLastName} onChange={lastNameInputChangeHandler} onBlur={lastNameInputBlurHandler} />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
      {nameInputHasError && lastNameInputHasError && <p>Input value cannot be empty</p>}
    </form>
  );
};

export default BasicForm;
