import { useState } from 'react'
import useInput from '../hooks/use-input'

const SimpleInput = (props) => {
  const { value: enteredName,
    hasError: nameInputHasError,
    valueChangeHandler: nameInputChangeHandler,
    inputBlurHandler: nameInputBlurHandler,
    isValid: enteredNameIsValid,
    reset: resetNameInput
  } = useInput(value => value.trim() !== "");


  const { value: enteredEmail,
    hasError: emailInputHasError,
    valueChangeHandler: eamilInputChangeHandler,
    inputBlurHandler: emailInputBlurHandler,
    isValid: enteredEmailIsValid,
    reset: resetEmailInput
  } = useInput(value => value.includes('@'));


  let formIsValid = false;
  if (enteredNameIsValid && enteredEmailIsValid) {
    formIsValid = true;
  }



  const formSubmitHandler = (event) => {
    event.preventDefault();

    resetNameInput();
    resetEmailInput();

    if (!enteredNameIsValid && !enteredEmailIsValid) {
      return
    }
    console.log(enteredName)
    setEnteredEmail("");
    setEnteredEmailTouched(false);
  }


  const nameInputClasses = nameInputHasError && emailInputIsInvalid ? 'form-control invalid' : 'form-control'

  return (
    <form onSubmit={formSubmitHandler} >
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={nameInputChangeHandler} value={enteredName}
          onBlur={nameInputBlurHandler} />
        <label htmlFor='email'>Your Email</label>
        <input type='email' id='email' onChange={emailInputChangeHandler} value={enteredEmail}
          onBlur={emailInputBlurHandler} />
        {nameInputHasError && emailInputIsInvalid && <p className='error-text'>Name must not be empty</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form >
  );
};

export default SimpleInput;
